INSERT INTO `t_contents` VALUES ('52', 'Giới thiệu chung', 'Chả mực - Chả cá đặc sản cao cấp Hạ Long', '<p>Khởi nguồn từ những phương thức chế biến truyền thống cổ truyền kết hợp c&ugrave;ng với sự s&aacute;ng tạo, nghi&ecirc;n cứu đ&atilde; cho ra một sản phẩm mang đậm chất hương vị của v&ugrave;ng biển Hạ Long. Sự kh&aacute;c biệt để tạo n&ecirc;n một thương hiệu chả mực Moon Food đ&oacute; ch&iacute;nh l&agrave; phương thức gi&atilde; tay truyền thống v&agrave; nặn thủ c&ocirc;ng từ đ&ocirc;i b&agrave;n tay t&agrave;i hoa, kh&eacute;o l&eacute;o của những nghệ nh&acirc;n l&agrave;m chả mực giỏi nhất tại Hạ Long v&agrave; l&agrave;m n&ecirc;n m&oacute;n Chả mực c&oacute; 1 kh&ocirc;ng 2, ho&agrave;n to&agrave;n kh&aacute;c biệt với những thương hiệu chả mực kh&aacute;c bởi độ gi&ograve;n, dai v&agrave; độ ngọt của biển...</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"413\" src=\"/upload_images/images/sp1.jpg\" width=\"550\" /></p>', null, '166', 'gioi-thieu', 'Giới thiệu', ',166,', ',gioi-thieu,', '1', 'gioi-thieu', 'images/contents/2018/07/23/original/logo_1532341230.png', null, null, '2018-07-23 12:20:30', '2018-07-23 12:20:30', null, '1', '0', '1', '1', null, '1', '0', null, '0', '0', '', '', '', '', '0', 'admin');
