INSERT INTO `t_products_copy` VALUES ('3120', 'Sofa Da MLEH-958T', null, null, 'sofa-da-mleh-958t', 'sofa-da-mleh-958t', '97', ',97,', null, 'Sofa Malaysia', 'sofa-malaysia', ',sofa-malaysia,', '0', '? <span style=\"color: #ff0000;\"><strong>Gói 3 năm làm sạch sofa 5 triệu đồn</strong><strong>g</strong></span>\r\n\r\n<strong>?</strong> <span style=\"color: #ff0000;\"><strong>++ ƯU ĐÃI khi đến Showroom</strong></span>', null, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>SOFA DA MLEH - 958T - THƯ GIÃN TUYỆT VỜI TRONG KHÔNG GIAN PHÒNG KHÁCH NHỎ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><strong><em>Cứng vừa đủ - lún vừa đủ</em></strong>, đó là điều bạn sẽ cảm nhận được khi vừa đặt mình xuống Sofa.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4338\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/sofa-mleh-95805.jpg\" alt=\"\" width=\"960\" height=\"645\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><em><strong>Độ nghiêng hoàn hảo:</strong></em> Ngả lưng một chút, độ nghiêng có tính toán cộng với kết cấu lưng ghế đặc biệt, vừa vặn một cách đáng ngạc nhiên, mang đến cảm giác thoải mái ngay lập tức, khiến người dùng không cần xoay ngang xoay dọc để tìm tư thế thư giãn nhất.</span></p>\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4337\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/sofa-mleh-95804.jpg\" alt=\"\" width=\"960\" height=\"640\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><em><strong>Bền bỉ, dễ chịu hơn từ da thật 100%: </strong></em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Toàn bộ da bề mặt tiếp xúc, da ở hông, ở hậu đều làm từ da bò cao cấp, được xử lý bằng công nghệ thuộc da tân tiến nhất. Do đó, sofa càng dùng lâu càng bóng đẹp, không dễ bong tróc như da công nghiệp, có thể sử dụng đến 7-10 năm.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4336\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/sofa-mleh-95803.jpg\" alt=\"Sofa MLEH 958\" width=\"960\" height=\"640\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Khi chạm hay ngồi thấy độ mát, mịn, rất thông thoáng chứ không thấy bí, rít như da công nghiệp.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><em><strong>Khung gỗ dầu cao cấp:</strong></em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Khung Sofa được làm từ gỗ dầu Malaysia, loại gỗ được mệnh danh \"ngâm trong nước 10 năm cũng không bị hỏng\" như một minh chứng về sự chắc chắn của Sofa.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4335\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/sofa-mleh-95802.jpg\" alt=\"\" width=\"960\" height=\"647\" /></span></p>\r\n<p style=\"text-align: justify;\">Với thiết kế sáng màu cùng kích thước thon gọn, chiều dài 2.45m, Sofa MLEH-958 hoàn hảo cho phòng khách nhỏ.</p>\r\n<p style=\"text-align: justify;\">Tuy nhiên, để lựa chọn là chính xác nhất, hãy đến trực tiếp showroom để chạm và cảm nhận sản phẩm, đồng thời nhận tư vấn trực tiếp từ kiến trúc sư của chúng tôi!</p>\r\n<p style=\"text-align: center;\">-----------------------</p>\r\n<p style=\"text-align: center;\"><strong>EUROHOME - KHẲNG ĐỊNH SỐ 1 SOFA DA</strong></p>\r\n<p style=\"text-align: center;\"><strong>Địa chỉ: Showroom tầng 1 tòa nhà Meco, ngõ 102 Trường Chinh, Đống Đa, Hà Nội </strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"> </span></p>', null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null, '0', null, '0', 'VND', '2017-10-26 17:05:15', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
INSERT INTO `t_products_copy` VALUES ('3130', 'Sofa Da MLEH-248D', null, null, 'sofa-da-mleh-248d', 'sofa-da-mleh-248d', '97', ',97,', null, 'Sofa Malaysia', 'sofa-malaysia', ',sofa-malaysia,', '0', '? <span style=\"color: #ff0000;\"><strong>Gói 3 năm làm sạch sofa 5 triệu đồn</strong><strong>g</strong></span>\r\n\r\n<strong>?</strong> <span style=\"color: #ff0000;\"><strong>++ ƯU ĐÃI khi đến Showroom</strong></span>', null, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>SOFA DA THẬT MLEH-248 - KHI NẰM SOFA TUYỆT HƠN CẢ GIƯỜNG</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢ Không giống những bộ sofa thông thường, MLEH - 248 sinh ra để \"nghỉ dưỡng\", dù ngồi, nằm hay đơn giản ngả lưng một chút, MLEH - 2248 vẫn luôn biết cách khiến gia chủ thoả mãn.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢ \"Chiếc gối\" được thiết kế đặc biệt ở vai sofa (hay còn gọi là tựa tay) đặc biệt êm hơn những bộ phận còn lại, ví như \"<span class=\"text_exposed_show\">tựa đầu trên hàng ghế sau chiếc Mercedes S500\".</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter wp-image-3229 size-full\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/mleh2484.jpg\" alt=\"\" width=\"900\" height=\"600\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Khoảng cách so với bề mặt ngồi không quá cao, được tính toán \"hoàn hảo\" giúp bạn có thể thoải mái ngả lưng ngủ một chút khi đang làm việc hoặc nằm xem tivi đều vô cùng dễ chịu.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><strong><em>➢ Mướt mịn, thoáng mát từ da bò cao cấp:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Nếu sofa giả da kết cấu từ nhựa, không thấm nước, thường gây cảm giác bí rít, thậm chí nóng bức khi ngồi thì MLEH - 248 với chất liệu da thật sẽ xóa bỏ điều đó.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter wp-image-3230 size-full\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/mleh2485.jpg\" alt=\"\" width=\"900\" height=\"600\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Bản chất trên bề mặt da thật rất nhiều lỗ chân lông, ấm vào mùa đông, mát vào mùa hè, rất thoáng mát. Cộng thêm da được nhập hoàn toàn từ Ý - đất nước số 1 về trình độ thuộc da, do đó da sờ mát, mịn như nhung.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><strong><em>➢ Hệ thống đệm mút cao cấp, lò xo túi </em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Không chỉ kết cấu đệm mút thông thường, MLEH - 248 có kết cấu đặc biệt gồm 2 tấm đệm mút, giữa được đan cài hệ thống lò xo túi. Do đó, sofa có độ đàn hồi hoàn hảo, khi đang ngồi rồi đứng dậy, sofa sẽ ngay lập tức trở về trạng thái ban đầu. Qua thời gian không sợ bị lún, xụp, bẹp.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter wp-image-3231 size-full\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/mleh2486.jpg\" alt=\"\" width=\"900\" height=\"600\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><strong><em>➢ Khung gỗ dầu cao cấp: </em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Điểm tạo nên chất liệu bền bỉ của sofa Malaysia chính là khung gỗ dầu - loại gỗ chỉ có ở Malaysia, được đánh giá cao về độ cứng, chắc và độ bền.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter wp-image-3228 size-full\" src=\"https://noithateurohome.com/wp-content/uploads/2017/10/mleh2483.jpg\" alt=\"\" width=\"900\" height=\"600\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Qua xử lý chống thấm, mối mọt, khung gỗ sau 10 năm vẫn hoàn toàn không vấn đề gì, không có hiện tượng mọt, kêu cót két, xập xệ như khung gỗ thông thường dùng trong sofa Việt Nam, sofa Trung Quốc.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Bạn đã sẵn sàng thả mình nằm dài trên Sofa, gối đầu vào chiếc “gối Mercedes” và xem tivi trong cảm giác êm ái, thư thái đến tột cùng chưa?</span></p>\r\n<p style=\"text-align: justify;\">Hãy đến trực tiếp Showroom Nội Thất EUROHOME để chạm và cảm nhận sản phẩm! Đồng thời, tại Showroom, Kiến trúc sư của chúng tôi luôn sẵn sàng để tư vấn giúp bạn!</p>\r\n<p style=\"text-align: center;\">--------------------</p>\r\n<p style=\"text-align: center;\"><strong>EUROHOME - KHẲNG ĐỊNH SỐ 1 SOFA DA</strong></p>\r\n<p style=\"text-align: center;\"><strong>Địa chỉ: <span style=\"color: #ff0000;\">Showroom tầng 1 tòa nhà Meco, ngõ 102 Trường Chinh, Đống Đa, Hà Nội </span></strong></p>', null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null, '0', null, '0', 'VND', '2017-10-26 17:16:35', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
