INSERT INTO `t_products_copy1` VALUES ('3395', 'Sofa Da MLEH-234D', null, null, 'sofa-da-mleh-234d', 'sofa-da-mleh-234d', '97', ',97,', null, 'Sofa Malaysia', 'sofa-malaysia', ',sofa-malaysia,', '0', '', null, 'Được nhập khẩu trực tiếp từ nhà cung cấp Omega. Đây là mẫu sofa được thiết kế theo mẫu mới nhất của Italia, mang kiểu dáng hiện đại rất phù hợp với các căn hộ cao cấp tại Việt Nam.<img class=\"size-medium wp-image-3396 aligncenter\" src=\"https://noithateurohome.com/wp-content/uploads/2017/11/mleh-234d1-533x400.jpg\" alt=\"\" width=\"533\" height=\"400\" />\r\n\r\nSofa MLEH-234 được làm 100% da thật, có dày 0.9mm-1.1mm. Với kiểu dáng hiện đại, trang nhã và thẩm mỹ tinh tế, đây là mẫu sofa da hứa hẹn sẽ là lựa chon tốt cho ngôi nhà của bạn\r\n\r\n<img class=\"size-medium wp-image-3397 aligncenter\" src=\"https://noithateurohome.com/wp-content/uploads/2017/11/mleh-234d2-533x400.jpg\" alt=\"\" width=\"533\" height=\"400\" />\r\n\r\n&nbsp;', null, 'wp-content/uploads/2017/11/mleh-234d1-1.jpg', 'images/products/2018/04/04/original/mleh-234d1-1.jpg', null, null, null, null, null, null, null, null, null, null, 't_products_sofa', '49800000', '0', null, null, '0', null, '0', 'VND', '2017-11-10 11:54:25', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
INSERT INTO `t_products_copy1` VALUES ('3711', 'Sofa MLEH-960', null, null, 'sofa-da-that-malaysia-nhap-khau-mleh-960', 'sofa-da-that-malaysia-nhap-khau-mleh-960', '97', ',97,', null, 'Sofa Malaysia', 'sofa-malaysia', ',sofa-malaysia,', '0', '? <span style=\"color: #ff0000;\"><strong>Gói 3 năm làm sạch sofa 5 triệu đồn</strong><strong>g</strong></span>\r\n\r\n<strong>?</strong> <span style=\"color: #ff0000;\"><strong>++ ƯU ĐÃI khi đến Showroom</strong></span>', null, '<h2 style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Sofa MLEH 960 - Tận cùng của sự thư giãn </strong></span></h2>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Sofa Malaysia có lẽ chưa bao giờ khiến người dùng thất vọng về trải nghiệm êm ái trong những thiết kế sofa sang trọng.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Sofa MLEH 960 thể hiện quyết tâm đem đến một \"bộ sofa massage\" khi nghiên cứu kĩ lưỡng đến từng chi tiết thói quen và form dáng của người châu Á, để tạo ra một thiết kế dễ chịu tuyệt đối.</span></p>\r\n<span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4155\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-96002.jpg\" alt=\"\" width=\"960\" height=\"640\" /></span>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><b>TỰA LƯNG VỚI 2 ĐIỂM CHẠM HOÀN HẢO</b></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Tựa lưng được thiết kế lạ mắt với 2 phần gối: gối trên căng phồng mềm mại, gối dưới nhỏ hơn, cứng hơn và check về phía trước.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Khi ngả lưng ra sau, điều kì diệu nằm ở chỗ, phần gối dưới cứng cáp tác động đúng vị trí thắt lưng - nơi thường xuyên khiến cơ thể đau nhức; gối trên mềm mại ôm trọn và nâng đỡ lưng trên và bả vai.</span></p>\r\n<span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4157\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-96004.jpg\" alt=\"\" width=\"960\" height=\"640\" /></span>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Kết hợp với khoảng cách từ mép ngoài đến tựa lưng khá ngắn, được tính toán chuẩn xác đến mức chỉ cần ngồi xuống thôi, lưng bạn đã có thể tiếp xúc ngay với tựa lưng. Chẳng cần điều chỉnh tư thế, bạn có thể cảm nhận ngay sự thư giãn ngay tức khắc.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><b>TRỌN VẸN TINH HOA SOFA MALAYSIA</b></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢<strong><em> Da thật 100%</em></strong>: Sofa MLEH 960 phủ những tấm da bò cao cấp, trọn vẹn cả trư<span class=\"text_exposed_show\">ớc - sau - mạn sườn. Chất da thật mềm hơn, mịn hơn, thông thoáng, không tạo cảm giác bí.</span></span></p>\r\n<span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4158\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-96006.jpg\" alt=\"Sofa MLEH-960\" width=\"960\" height=\"640\" /></span>\r\n<div class=\"text_exposed_show\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢<em><strong> Hệ thống lò xo túi</strong></em> đặc biệt giữa 2 lớp đệm theo đúng tiêu chuẩn Malaysia, đàn hồi tốt hơn, không lo xẹp lún, giữ cho phom dáng Sofa ổn định sau thời gian dài sử dụng.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢ <em><strong>Khung Sofa</strong></em> được làm từ GỖ DẦU CAO CẤP bậc nhất tại Malaysia với khả năng chịu tải tốt, không bị mối mọt, cong vênh. Điều này khiến cho Sofa Da thật MLEH 960 bền bỉ suốt 20 năm.</span></p>\r\n\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><strong>THIẾT KẾ TỐI ƯU CHO PHÒNG KHÁCH VỪA VÀ NHỎ</strong></span></p>\r\n\r\n<div class=\"text_exposed_show\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Mặc dù có chiều dài 2690 cm nhưng Sofa MLEH 960 lại có form dáng thon gọn do kích thước bề ngang chỉ có 910 cm, chiều dài góc chỉ có 1600 cm.</span></p>\r\n<span style=\"font-size: 12pt;\"><img class=\"aligncenter wp-image-4156 size-full\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-96003.jpg\" alt=\"Sofa MLEH-960\" width=\"960\" height=\"640\" /></span>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><em><strong>Tựa tay</strong></em> cũng đặc biệt được thiết kế nhỏ hơn để dành chỗ cho vị trí ngồi, giúp bạn có thể tiếp được nhiều khách hơn.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Vì vậy, Sofa MLEH 960 chính là lựa chọn hoàn hảo cho phòng khách vừa và nhỏ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Một bộ Sofa sang trọng, đẳng cấp tỏa sáng không gian phòng khách và thoải mái tột cùng, xua tan đi mọi mệt mỏi căng thẳng, bạn quyết định đầu tư chứ?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Hãy đến trực tiếp Showroom Nội thất EUROHOME để chạm và trải nghiệm sản phẩm. Tại Showroom, các kiến trúc sư của chúng tôi luôn sẵn sàng tư vấn để bạn có sự lựa chọn tốt nhất!</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\">----------------</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>EUROHOME - KHẲNG ĐỊNH SỐ 1 SOFA DA</strong></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Hotline: 094.370.6666</strong></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Địa chỉ:<span style=\"color: #ff0000;\"> Tầng 1 tòa nhà Meco, ngõ 102 Trường Chinh, Đống Đa, Hà Nội</span></strong></span></p>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n</div>', null, 'wp-content/uploads/2017/12/avatarmleh-960-1.jpg', 'images/products/2018/04/04/original/avatarmleh-960-1.jpg', null, null, null, null, null, null, null, null, null, null, 't_products_sofa', '45500000', '0', null, null, '0', null, '0', 'VND', '2017-12-14 16:23:09', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
INSERT INTO `t_products_copy1` VALUES ('3900', 'Sofa da thật MLEH-973', null, null, 'sofa-da-that-mleh-973', 'sofa-da-that-mleh-973', '97', ',97,', null, 'Sofa Malaysia', 'sofa-malaysia', ',sofa-malaysia,', '0', '<span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\"><strong>Trưng bày tại: <span style=\"color: #ff0000;\">Tầng 1 tòa Meco, ngõ 102 Trường Chinh, Đống Đa, HN </span></strong></span>\r\n<span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\"> <strong> >> Nhận ngay ƯU ĐÃI đặc biệt tại điểm bán!</strong></span>', null, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Sofa da thật MLEH - 973 - QUYẾN RŨ KIỂU TÂY PHƯƠNG</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Quyền lực và thời Trang -  ý tưởng này được thể hiện xuyên suốt trong toàn bộ thiết kế của MLEH-973 - mẫu sofa mới nhất từ thương hiệu danh tiếng Malaysia.</span></p>\r\n[embed]https://www.youtube.com/watch?v=85UY7dZDGqU[/embed]\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Đó chính là khí chất đặc biệt sau tướng mạo \"oai nghiêm, bệ vệ\" toát ra từ hình khối cứng cáp, kích thước 3000*1620*720. Bề mặt da được kéo căng, không chút gợn, thể hiện đúng chất sang trọng của một bộ sofa cao cấp.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Trên gam màu và phom dáng hiện đại là sự khéo léo đưa vào những tiết tấu cổ điển, chỉ đơn giản là những đường may kiểu caro với các điểm giao hơi trũng xuống cũng đủ khiến bộ sofa tuy \"đồ sộ\" mà vẫn đầy tinh tế.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><em><strong>Đi sâu hơn vào chi tiết, Sofa MLEH-973 cũng có nhiều điều để nói:</strong></em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Được nhập khẩu nguyên chiếc từ Malaysia, Sofa Da thật MLEH-973 mang đầy đủ tinh hoa và công nghệ sản xuất Sofa của thương hiệu hàng đầu thế giới.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4184\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-97306-min.jpg\" alt=\"\" width=\"960\" height=\"640\" /></span></p>\r\n\r\n<div class=\"text_exposed_show\" style=\"text-align: justify;\">\r\n\r\n<span style=\"font-size: 12pt;\">➢ Từng đường chỉ được may tỉ mỉ, trau chuốt tựa như một tác phẩm nghệ thuật đậm chất Italy, kết hợp với kiểu dáng cứng cáp hiện đại, chỉn chu tạo nên 1 vẻ lịch lãm và sức hút khó cưỡng.</span>\r\n\r\n<span style=\"font-size: 12pt;\">➢ <strong><em>Da thật 100%</em></strong>: Những miếng da bò cao cấp được bọc toàn bộ sofa (mặt tiếp xúc, hông, hậu) mang đến trải nghiệm và độ bền hoàn toàn khác biệt.</span>\r\n\r\n<span style=\"font-size: 12pt;\">Chỉ cần đưa tay vuốt nhẹ trên bề mặt da, các vị khách đến chơi nhà sẽ hiểu rằng bạn là một người có lối sống tinh tế, đẳng cấp nhưng vẫn luôn gần gũi và chân thật như chất DA THẬT của bộ Sofa này.</span>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4183\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-97305-min.jpg\" alt=\"\" width=\"960\" height=\"640\" /></span></p>\r\n<span style=\"font-size: 12pt;\">➢<em><strong> Hệ thống lò xo túi</strong></em> đặc biệt giữa 2 lớp đệm theo đúng tiêu chuẩn Malaysia, đàn hồi tốt hơn, không lo xẹp lún, giữ cho phom dáng Sofa ổn định sau thời gian dài sử dụng.</span>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><strong><em>➢ Khung gỗ dầu cao cấp:</em>  </strong>gỗ dầu cao cấp chỉ có tại Malaysia với đặc tính cứng, khả năng chịu tải tốt, được mệnh danh là “ngâm 10 năm trong nước không mục ruỗng” như một minh chứng rõ ràng về tuổi thọ (&gt;10 năm) của Sofa.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢ <strong><em>Tự</em><em>a</em><em> gật gù</em><em> linh hoạt</em> </strong>giúp bạn dễ dàng điều chỉnh theo tư thế ngồi yêu thích.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><img class=\"aligncenter size-full wp-image-4182\" src=\"https://noithateurohome.com/wp-content/uploads/2017/12/sofa-mleh-97304-min.jpg\" alt=\"\" width=\"960\" height=\"640\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">➢ Sofa MLEH-973 sang hơn, đẳng cấp hơn với chân ghế inox sáng bóng bao quanh Sofa, tạo thế vững chãi, quyền lực cho gia chủ khi ngồi. Chỉ cần đặt Sofa dưới ánh sáng đèn điện, cả phòng khách của bạn sẽ sáng bừng sức sống!</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\">-----------</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">Nếu bạn đang sở hữu một căn phòng khách lớn, Sofa MLEH - 973 chắc chắn là khoản đầu tư tốt nhất - khẳng định địa vị và đẳng cấp không gian nhà bạn.</span></p>\r\n<span style=\"font-size: 12pt;\">Tuy nhiên, để lựa chọn của mình sáng suốt và xứng đáng, hãy đến trực tiếp showroom của chúng tôi để được tư vấn từ kiến trúc sư và tham khảo thêm nhiều mẫu mã và cảm nhận sự khác biệt!</span>\r\n\r\n<span style=\"font-size: 12pt;\"><em><strong>Địa chỉ: <span style=\"color: #3366ff;\">Showroom Nội thất EUROHOME tầng 1 tòa nhà Meco, ngõ 102 Trường Chinh, Đống Đa, HN</span></strong></em></span>\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n</div>', null, 'wp-content/uploads/2017/12/avatarmleh-973-1.jpg', 'images/products/2018/04/04/original/avatarmleh-973-1.jpg', null, null, null, null, null, null, null, null, null, null, 't_products_sofa', '53000000', '0', null, null, '0', null, '0', 'VND', '2017-12-22 14:41:36', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
INSERT INTO `t_products_copy1` VALUES ('4246', 'Sofa da thật MLEH-248T', null, null, 'sofa-da-that-mleh-248t', 'sofa-da-that-mleh-248t', '112', ',112,', null, 'Sofa Da Thật', 'sofa-da-that', ',sofa-da-that,', '0', '? <span style=\"color: #ff0000;\"><strong>Gói 3 năm làm sạch sofa 5 triệu đồn</strong><strong>g</strong></span>\r\n\r\n<strong>?</strong> <span style=\"color: #ff0000;\"><strong>++ ƯU ĐÃI khi đến Showroom</strong></span>', null, '<p style=\"text-align: center;\"><strong>SOFA DA THẬT MLEH-248 - KHI NẰM SOFA TUYỆT HƠN CẢ GIƯỜNG</strong></p>\r\n<p style=\"text-align: justify;\">➢ Không giống những bộ sofa thông thường, MLEH - 248 sinh ra để \"nghỉ dưỡng\", dù ngồi, nằm hay đơn giản ngả lưng một chút, MLEH - 2248 vẫn luôn biết cách khiến gia chủ thoả mãn.</p>\r\n[embed]https://www.youtube.com/watch?v=9VYBIyjZ8ho&feature=youtu.be[/embed]\r\n<p style=\"text-align: justify;\">➢ \"Chiếc gối\" được thiết kế đặc biệt ở vai sofa (hay còn gọi là tựa tay) đặc biệt êm hơn những bộ phận còn lại, ví như \"<span class=\"text_exposed_show\">tựa đầu trên hàng ghế sau chiếc Mercedes S500\".</span></p>\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4251\" src=\"https://noithateurohome.com/wp-content/uploads/2018/01/sofa-mleh-248t04.jpg\" alt=\"\" width=\"2880\" height=\"2160\" /></p>\r\n<p style=\"text-align: justify;\">Khoảng cách so với bề mặt ngồi không quá cao, được tính toán \"hoàn hảo\" giúp bạn có thể thoải mái ngả lưng ngủ một chút khi đang làm việc hoặc nằm xem tivi đều vô cùng dễ chịu.</p>\r\n<p style=\"text-align: justify;\"><strong><em>➢ Mướt mịn, thoáng mát từ da bò cao cấp:</em></strong></p>\r\n<p style=\"text-align: justify;\">Nếu sofa giả da kết cấu từ nhựa, không thấm nước, thường gây cảm giác bí rít, thậm chí nóng bức khi ngồi thì MLEH - 248 với chất liệu da thật sẽ xóa bỏ điều đó.</p>\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4252\" src=\"https://noithateurohome.com/wp-content/uploads/2018/01/sofa-mleh-248t05.jpg\" alt=\"\" width=\"2880\" height=\"2160\" /></p>\r\n<p style=\"text-align: justify;\">Bản chất trên bề mặt da thật rất nhiều lỗ chân lông, ấm vào mùa đông, mát vào mùa hè, rất thoáng mát. Cộng thêm da được nhập hoàn toàn từ Ý - đất nước số 1 về trình độ thuộc da, do đó da sờ mát, mịn như nhung.</p>\r\n<p style=\"text-align: justify;\"><strong><em>➢ Hệ thống đệm mút cao cấp, lò xo túi </em></strong></p>\r\n<p style=\"text-align: justify;\">Không chỉ kết cấu đệm mút thông thường, MLEH - 248 có kết cấu đặc biệt gồm 2 tấm đệm mút, giữa được đan cài hệ thống lò xo túi. Do đó, sofa có độ đàn hồi hoàn hảo, khi đang ngồi rồi đứng dậy, sofa sẽ ngay lập tức trở về trạng thái ban đầu. Qua thời gian không sợ bị lún, xụp, bẹp.</p>\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4250\" src=\"https://noithateurohome.com/wp-content/uploads/2018/01/sofa-mleh-248t03.jpg\" alt=\"\" width=\"2880\" height=\"2160\" /></p>\r\n<p style=\"text-align: justify;\"><strong><em>➢ Khung gỗ dầu cao cấp: </em></strong></p>\r\n<p style=\"text-align: justify;\">Điểm tạo nên chất liệu bền bỉ của sofa Malaysia chính là khung gỗ dầu - loại gỗ chỉ có ở Malaysia, được đánh giá cao về độ cứng, chắc và độ bền.</p>\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4248\" src=\"https://noithateurohome.com/wp-content/uploads/2018/01/sofa-mleh-248t01.jpg\" alt=\"\" width=\"2880\" height=\"2160\" /></p>\r\n<p style=\"text-align: justify;\">Qua xử lý chống thấm, mối mọt, khung gỗ sau 10 năm vẫn hoàn toàn không vấn đề gì, không có hiện tượng mọt, kêu cót két, xập xệ như khung gỗ thông thường dùng trong sofa Việt Nam, sofa Trung Quốc.</p>\r\n<p style=\"text-align: justify;\">Bạn đã sẵn sàng thả mình nằm dài trên Sofa, gối đầu vào chiếc “gối Mercedes” và xem tivi trong cảm giác êm ái, thư thái đến tột cùng chưa?</p>\r\nHãy đến trực tiếp Showroom Nội Thất EUROHOME để chạm và cảm nhận sản phẩm! Đồng thời, tại Showroom, Kiến trúc sư của chúng tôi luôn sẵn sàng để tư vấn giúp bạn!\r\n<p style=\"text-align: center;\">--------------------</p>\r\n<p style=\"text-align: center;\"><strong>EUROHOME - KHẲNG ĐỊNH SỐ 1 SOFA DA</strong></p>\r\n<p style=\"text-align: center;\"><strong>Địa chỉ: <span style=\"color: #ff0000;\">Showroom tầng 1 tòa nhà Meco, ngõ 102 Trường Chinh, Đống Đa, Hà Nội </span></strong></p>', null, 'wp-content/uploads/2018/01/avatarmleh-248t-2.jpg', 'images/products/2018/04/04/original/avatarmleh-248t-2.jpg', null, null, null, null, null, null, null, null, null, null, 't_products_sofa', '44000000', '0', null, null, '0', null, '0', 'VND', '2018-01-16 17:31:51', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, '1', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
INSERT INTO `t_products_copy1` VALUES ('4418', 'Sofa ILEH-RB66', null, null, 'sofa-mleh-rb66', 'sofa-mleh-rb66', '101', ',101,', null, 'Sofa Italia', 'sofa-italia', ',sofa-italia,', '0', '? <span style=\"color: #ff0000;\"><strong>Gói 3 năm làm sạch sofa 5 triệu đồn</strong><strong>g</strong></span>\r\n\r\n<strong>?</strong> <span style=\"color: #ff0000;\"><strong>++ ƯU ĐÃI khi đến Showroom</strong></span>', null, '<h2 style=\"text-align: center;\">Sofa ILEH-RB66 - Vẹn nguyên giá trị từ sofa “made in Italy”</h2>\r\n<p style=\"text-align: justify;\">Đẳng cấp và khác biệt trong từng đường kim mũi chỉ, sự mềm mại kì diệu của bề mặt da đến từ cái nôi của ngành công nghiệp thuộc da thế giới. Mẫu Sofa Robin 66 được tạo ra dưới bàn tay khéo léo của nghệ nhân Ý xuất hiện như một tác phẩm nghệ thuật hoàn mỹ.</p>\r\n<p style=\"text-align: justify;\">Ngôn từ thôi là chưa đủ để bàn về giá trị thẩm mỹ cũng như sự thích thú khi vừa ngồi xuống \"tác phẩm\" này. Cảm giác lâng lâng, bồng bềnh trên mây -  khó thể dùng từ chính xác để miêu tả được sự khoan khoái tuyệt diệu này.</p>\r\n[embed]https://www.youtube.com/watch?v=m9EuBLcuH3U&feature=youtu.be[/embed]\r\n<p style=\"text-align: justify;\">Có một câu nói nổi tiếng của Milton Glaser: “Sẽ luôn có ba kiểu phản ứng khác nhau dành cho một thiết kế  – Yes, no, và WOW!”, và WOW chính là thán từ bất cứ ai cũng phải thốt lên khi trải nghiệm Sofa ILEH RB66 một lần trong đời.</p>\r\n<p style=\"text-align: justify;\"><strong>Thẩm mỹ và khoa học</strong></p>\r\n<p style=\"text-align: justify;\">Ở Sofa ILEH RB66 hội tụ cả 2 yếu tố tổng hòa: thẩm mỹ và khoa học. Vật liệu cũng như kĩ thuật chế tác được đặc biệt chú trọng.</p>\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4766\" src=\"https://noithateurohome.com/wp-content/uploads/2018/02/sofa-da-that-ileh-rb6606.jpg\" alt=\"\" width=\"960\" height=\"640\" /></p>\r\n<p style=\"text-align: justify;\">Chất da bò cao cấp bao phủ trọn vẹn tất cả các bề mặt sofa, độ bền vượt trội lên tới 5 năm. Chỉ cần sờ thôi, đó là sự khác biệt của chất da thuộc mà hàng trăm năm rồi, các quốc gia phân tích, học hỏi từ Ý nhưng khó lòng bắt kịp.</p>\r\n<p style=\"text-align: justify;\">Về kết cấu, Sofa ILEH-RB66 vững chắc nhờ khung gỗ sồi Bắc Âu rắn chắc. Nhưng đó không phải điều quan trọng nhất. Bí mật của bộ sofa Ý bền vững tới 20 năm nằm ở sự tính toán tỉ lệ một cách chính xác. Và điều này thuộc về tuyệt kĩ bí truyền của nước Ý.</p>\r\n<strong>Tôn tạo đẳng cấp không gian</strong>\r\n\r\nCó lẽ không cần thiết phải bàn nhiều về chất lượng của Sofa ILEH-RB66, cũng không cần thiết phải dùng những mỹ từ để nói về đẳng cấp của đứa con tinh thần từ nước Ý. Chỉ cần đặt trong không gian thôi, Sofa ILEH-RB66 cũng tự biết cách trở thành trung tâm thế giới.\r\n<p style=\"text-align: center;\"><img class=\"aligncenter size-full wp-image-4762\" src=\"https://noithateurohome.com/wp-content/uploads/2018/02/sofa-da-that-ileh-rb6602.jpg\" alt=\"sofa da thật ILEH-RB66\" width=\"960\" height=\"640\" /></p>\r\nTuy nhiên, bộ sofa dài tới 3.06m nên kén chọn một phòng khách đủ lớn đấy!\r\n\r\nNếu bạn muốn tận hưởng \"cảm giác thần tiên\" từ một mẫu sofa Italia đúng nghĩa, hãy đến Showroom Nội thất EUROHOME để chiêm ngưỡng và trải nghiệm Sofa ILEH-RB66 nhé!\r\n<ul>\r\n 	<li><strong>Cam kết đến cùng nguồn gốc sofa: sofa nhập khẩu nguyên chiếc Italia</strong></li>\r\n 	<li><strong>Da thật 100% - Đền gấp 10 lần nếu phát hiện da trộn, giả da</strong></li>\r\n</ul>\r\n<p style=\"text-align: center;\">---------------------</p>\r\n<p style=\"text-align: center;\"><span style=\"color: #990909;\"><strong>NỘI THẤT EUROHOME - SỐ 1 SOFA DA THẬT</strong></span></p>\r\n<p style=\"text-align: center;\"><strong>Địa chỉ: <span style=\"color: #ff0000;\">tầng 1 tòa Meco, ngõ 102 Trường Chinh, Đống Đa, Hà Nội</span></strong></p>\r\n<p style=\"text-align: center;\"><strong>HOTLINE: <span style=\"color: #ff0000;\">094.370.6666</span></strong></p>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;', null, 'wp-content/uploads/2018/02/avatar-sofa-da-that-ileh-rb6601.jpg', 'images/products/2018/04/04/original/avatar-sofa-da-that-ileh-rb6601.jpg', null, null, null, null, null, null, null, null, null, null, 't_products_sofa', '89000000', '93800000', null, null, '0', null, '0', 'VND', '2018-02-02 15:55:12', null, '1', null, '0', '0', '0', null, null, '', '0', null, '', null, null, null, null, null, '0', '0', null, '0', null, null, null, null, '1', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', null, null, null);
INSERT INTO `t_products_copy1` VALUES ('4432', 'Sofa ILEH-VD68', null, null, 'sofa-ileh-vd68', 'sofa-ileh-vd68', '101', ',101,', null, 'Sofa Italia', 'sofa-italia', ',sofa-italia,', '0', '', null, '<p style=\"text-align: justify;\">Nằm trong bộ sưu tập Verdi từ thương hiệu Trend Italiano, Sofa ILEH-VD68 hiện diện đầy tinh tế, thể hiện đẳng cấp tuyệt đối cho không gian.</p>\r\n<p style=\"text-align: justify;\">Hướng tới đơn giản trong thiết kế và tinh tế trong chi tiết, Sofa ILEH-VD68 chính là lựa chọn tối ưu cho những phòng khách chung cư hiện đại.</p>\r\n<p style=\"text-align: justify;\"><strong>Tên sản phẩm: ILEH-VD68\r\n</strong></p>\r\n<p style=\"text-align: justify;\"><strong>Nguồn gốc: Nhập khẩu nguyên chiếc từ Italia  </strong></p>\r\n<p style=\"text-align: justify;\">Chất liệu vỏ bọc: 100% da thật (da bò cao cấp Italia) với độ dày miếng da 1.2mm - 1.5mm. Bọc toàn bộ trước, sau, hông sofa.</p>\r\n<p style=\"text-align: justify;\"><b>Khung gỗ tự nhiên: </b>Gỗ dẻ gai (khung chính), kèm theo một số bộ phận kết nỗi từ gỗ ván ép, gỗ dán</p>\r\n<p style=\"text-align: justify;\"><strong>Chế độ bảo hành:</strong></p>\r\n\r\n<ul style=\"text-align: justify;\">\r\n 	<li>Bảo hành 20 năm khung sofa</li>\r\n 	<li>Bảo hành 2 năm bề mặt da</li>\r\n 	<li>Đền 10 lần nếu phát hiện da trộn, giả da</li>\r\n 	<li>Chế dộ sofa tự làm sạch duy nhất tại Việt Nam - miễn phí 3 năm vệ sinh, làm sạch sofa</li>\r\n</ul>\r\n<p style=\"text-align: center;\">------------------</p>\r\n<p style=\"text-align: center;\"><strong>NỘI THẤT EUROHOME - SỐ 1 SOFA DA THẬT</strong></p>\r\n<p style=\"text-align: center;\"><strong>Địa chỉ:<span style=\"color: #ff0000;\"> tầng 1 tòa Meco, ngõ 102 Trường Chinh, Đống Đa, HN</span></strong></p>\r\n<p style=\"text-align: center;\"><strong>Hotline: <a href=\"tel:0943706666\" target=\"_blank\" rel=\"noopener\">094.370.6666</a></strong></p>', null, 'wp-content/uploads/2018/02/sofa-ileh-vd-68.jpg', 'images/products/2018/04/04/original/sofa-ileh-vd-68.jpg', null, n