INSERT INTO `t_projects` VALUES ('10', '7', 'san-bay---nha-ga', null, 'Nhà ga T2 Nội Bài', 'Công trình nhà ga T2_ Nội Bài', 'cong-trinh-nha-ga-t2_-noi-bai', 'images/projects/2013/11/20/original/t2_1384930984.jpg', 'Taisei & Vinaconex', '', '', null, 'Cung cấp và lắp đặt hệ thống HVAC của Chiller', '7-2013 đến 4-2014', null, '<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: Arial, Verdana, Tahoma; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 12px; line-height: 15.6px;\">VIDIC l&agrave; đơn vị thầu phụ thi c&ocirc;ng hạng mục HVAC, hệ thống th&ocirc;ng&nbsp;gi&oacute;, hệ thống&nbsp;ống chiller v&agrave; ống đồng cho hệ thống điều h&ograve;a kh&ocirc;ng kh&iacute; trung t&acirc;m&nbsp;cho c&ocirc;ng tr&igrave;nh Nh&agrave; ga T2 - S&acirc;n bay quốc tế Nội B&agrave;i.</span></span></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: Arial, Verdana, Tahoma; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Nh&agrave; ga h&agrave;nh kh&aacute;ch T2 - Nội B&agrave;i&nbsp;gồm&nbsp;4 tầng với diện t&iacute;ch mặt bằng khoảng 140.000 m2, được thiết kế theo m&ocirc; h&igrave;nh dạng c&aacute;nh với &yacute; tưởng h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n, tận dụng tối đa &aacute;nh s&aacute;ng tự nhi&ecirc;n, tiết kiệm năng lượng. Nh&agrave; ga sẽ được đầu tư hệ thống thiết bị, d&acirc;y chuyền c&ocirc;ng nghệ h&agrave;ng kh&ocirc;ng ti&ecirc;n tiến, theo ti&ecirc;u chuẩn quốc tế.</span></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: Arial, Verdana, Tahoma; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Theo Tổng c&ocirc;ng ty Cảng h&agrave;ng kh&ocirc;ng miền Bắc, lưu lượng vận chuyển h&agrave;ng kh&ocirc;ng tại s&acirc;n bay Nội B&agrave;i đ&atilde; vượt qu&aacute; c&ocirc;ng suất của nh&agrave; ga T1 từ năm 2007. Sản lượng năm 2010 đạt tr&ecirc;n 9,5 triệu h&agrave;nh kh&aacute;ch v&agrave; dự kiến đạt 11 triệu h&agrave;nh kh&aacute;ch v&agrave;o năm 2014. Nh&agrave; ga T2 sau khi ho&agrave;n th&agrave;nh sẽ g&oacute;p phần giải quyết t&igrave;nh trạng qu&aacute; tải của nh&agrave; ga T1, tạo diện mạo mới cho s&acirc;n bay Nội B&agrave;i.</span></span></p>', '0', '5', '2013-09-26 15:54:10', '2014-09-08 15:27:29', 'Sân bay - Nhà ga', ',7,', ',san-bay---nha-ga,', '9', '2', '1', null, '0', '0', '0', null, '0', '0', '0', null, null, null, null, null);
